import { useParams } from "react-router-dom"
import style from "./crud.module.css"
import { useEffect, useState } from "react"
import axios from "axios"

const EditUser=()=>{
    let obj=useParams()
    const [name ,setName] =useState("")
    const [salary ,setSalary] =useState("")
    const [company ,setCompany] =useState("")
    useEffect(()=>{
        axios.get(`http://localhost:5004/employees/${obj.abc}`)
        .then((there)=>{
           setName(there.data.empName)
           setSalary(there.data.empSalary)
           setCompany(there.data.empCompany)
        })
        .catch(()=>{console.log("error");})
    },[])
    let edit =()=>{
        
            axios.put(`http://localhost:5004/employees/${obj.abc}`)
            .then((there)=>{
               setName(there.data.empName)
               setSalary(there.data.empSalary)
               setCompany(there.data.empCompany)
            })
            .catch(()=>{console.log("error");})
      
    }

    return(
      
        <div id={style.EditUser}>

            <div id={style.formDiv}>

            <form onSubmit={edit}>
                <h1>Edit-User</h1>
                <label>NAME</label>
                
                <br/><br/>

                <input type="text" value={name} />

                <br/><br/>
                <label>SALARY</label>
                
                <br/><br/>

                <input type="text" value={salary} />

                <br/><br/>
                <label>COMPANY</label>
                
                <br/><br/>

                <input type="text" value={company} />
                
                <br/><br/>
                <button type="submit">Submit</button>

            </form>
           </div>
        </div>
    )
}
export default EditUser
